const cloudinary = require('cloudinary').v2;
const intoStream = require('into-stream');
const { errors } = require('strapi-plugin-upload');

module.exports = {
  init(config) {
    cloudinary.config(config);

    return {
      upload(file, customConfig = {}) {
        return new Promise((resolve, reject) => {
          const upload_stream = cloudinary.uploader.upload_stream(
            {
              resource_type: 'video',
              public_id: file.hash,
              ...customConfig,
              transformation: [
                {
                  overlay: `video:${file.hash}`,
                  flags: 'splice',
                  effect: 'reverse',
                },
                {
                  overlay: `video:${file.hash}`,
                  flags: 'splice',
                  effect: 'accelerate:-50',
                },
                { effect: 'brightness:10', radius: 'max' },
              ],
            },
            (err, image) => {
              if (err) {
                if (err.message.includes('File size too large')) {
                  return reject(errors.entityTooLarge());
                }
                return reject(
                  errors.unknownError(
                    `Error uploading to cloudinary: ${err.message}`
                  )
                );
              }

              if (image.resource_type === 'video') {
                file.previewUrl = cloudinary.url(`${image.public_id}.gif`, {
                  video_sampling: 6,
                  delay: 200,
                  width: 250,
                  crop: 'scale',
                  resource_type: 'video',
                });

                file.url = cloudinary.url(`${image.public_id}.mp4`, {
                  quality: 'auto:eco',
                  width: 200,
                  crop: 'scale',
                  resource_type: 'video',
                });
              } else {
                file.url = image.secure_url;
              }

              file.provider_metadata = {
                public_id: image.public_id,
                resource_type: image.resource_type,
              };
              resolve();
            }
          );

          intoStream(file.buffer).pipe(upload_stream);
        });
      },
      async delete(file, customConfig = {}) {
        try {
          const { resource_type, public_id } = file.provider_metadata;
          const response = await cloudinary.uploader.destroy(public_id, {
            invalidate: true,
            resource_type: resource_type || 'image',
            ...customConfig,
          });

          if (response.result !== 'ok' && response.result !== 'not found') {
            throw errors.unknownError(
              `Error deleting on cloudinary: ${response.result}`
            );
          }
        } catch (error) {
          throw errors.unknownError(
            `Error deleting on cloudinary: ${error.message}`
          );
        }
      },
    };
  },
};
